namespace ZExt\Debug\Assets;

/**
 * Icons pack for the debug bar
 * 
 * @category   ZExt
 * @package    Debug
 * @subpackage Assets
 * @author     Mike.Mirten
 * @version    1.0
 */
class Icons {
	
	const ELEPHANT  = '';
	const WARNING   = '';
	const ALERT     = '';
	const BUBBLE    = '';
	const BUG       = '';
	const OK        = '';
	const CANCEL    = '';
	const CARD      = '';
	const CHIP      = '';
	const CHIP2     = '';
	const CHART     = '';
	const CLOCK     = '';
	const CUBES     = '';
	const COOKIE    = '';
	const DB        = '';
	const DBMONGO   = '';
	const DRIVE     = '';
	const FLOPPY    = '';
	const HDD       = '';
	const INFO      = '';
	const PUZZLE    = '';
	const BULB      = '';
	const MONITOR   = '';
	const PACKAGE   = '';
	const RELOAD    = '';
	const SCRIPT    = '';
	const SECURE    = '';
	const TERMINAL  = '';
	const ARROWUP   = '';
	const ARROWDOWN = '';
	const LIGHTNING = '';
	const PHALCON   = '';
	
	/**
	 * Get the icon as base64 encoded string
	 * 
	 * @param  string $name
	 * @return string
	 */
	static public function getIcon($name) {
		return 'data:image/png;base64,' . constant('self::' . strtoupper($name));
	}
	
}